// Body of the request
{
  "data": {
    "type": "RetailMediaReportRequest",
    "attributes": {
      "reportType": string, // the name of the report being requested
      //only one of "id" or "ids" can be provided for a request
      "id": string, // the campaign id to select
      "ids": Array[string], //the campaign ids to select
      "startDate": string, // YYYY-MM-DD format
      "endDate": string, // YYYY-MM-DD format
      "timeZone": string, // examples: 'Europe/London', 'Asia/Tokyo', 'America/New_York'
      // both attribution windows must be specified when one is specified
      "clickAttributionWindow": "7D", "14D", or "30D"  // optional. defaults to campaign's click attribution window
      "viewAttributionWindow": "none", "1D", "7D", "14D", "30D" // optional. defaults to campaign's view attribution window
      "format": One of "json" (default),"json-compact","json-newline" or "csv" // output format, defaults to json-compact
    }
  }
}

